import React, { Component } from 'react';

class TaskForm extends Component {
	state = {
		title: '',
		body: ''
	}

	handleChange = (e) => {
		const {name, value} = e.target;
		this.setState({
			[name]: value
		});
	}

	handleSubmit = (e) => {
		e.preventDefault();
		this.props.addTask(this.state.title,this.state.body);
		this.setState({
			title: '',
			body: ''
		});
	} 

	render(){
		return(
			<form>
				<input type="text" placeholder="Ingrese titulo" name="title" value={this.state.title} onChange={this.handleChange} />
				<br />
				<br />
				<textarea placeholder="Ingrese descripcion" name="body" value={this.state.body} onChange={this.handleChange} />
				<br />
				<br />
				<button type="submit">Enviar</button>
			</form>
		);
	}
}

export default TaskForm;
