import React, { Component } from 'react';
// JSON
import tasks from './tasks.json';
// css
import './App.css';
//components
import Tasks from './components/Tasks';

class App extends Component {
  state = {
    tasks: tasks
  }

  taskDone = (id) => {
    console.log(id);
    const newTasks = this.state.tasks.map( task => {
      if(task.id === id){
        task.done = !task.done;
      }
      return task;
    });

    this.setState({
      tasks: newTasks
    });
  }

  render(){
    return(
      <div className="App">
        <h1>Hola mundo</h1>
        <Tasks tasks={this.state.tasks} taskDone={this.taskDone} />
      </div>
    );
  }
}


export default App;
