import React from 'react';

function Task(props) {
	const title = props.task.title;
	const body = props.task.body;
	const done = props.task.done;
	return(
		<div>
			<h2>{title}</h2>
			<p>{body}</p>
			<p>
				{done? 
					'Tarea terminada': 
					'Terminar tarea?' }
			</p>
		</div>
	);
}

export default Task;
